import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      {/* Top Bar */}
      <div className="bg-black text-white p-4 flex justify-between items-center">
        <button onClick={() => setIsOpen(true)} className="text-2xl">&#9776;</button>
        <h1 className="text-xl font-bold">Hair by AMR</h1>
      </div>

      {/* Sidebar */}
      <div className={`fixed top-0 left-0 h-full w-64 bg-white shadow-lg transform ${isOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 z-50`}>
        <div className="p-4 border-b flex justify-between items-center">
          <h2 className="text-lg font-semibold">Menu</h2>
          <button onClick={() => setIsOpen(false)} className="text-2xl">&times;</button>
        </div>
        <ul className="p-4 space-y-4 text-lg">
          <li><Link to="/" onClick={() => setIsOpen(false)}>Home</Link></li>
          <li><Link to="/shop" onClick={() => setIsOpen(false)}>Shop</Link></li>
          <li><Link to="/color-chart" onClick={() => setIsOpen(false)}>Hair Color Chart</Link></li>
          <li><Link to="/supplies" onClick={() => setIsOpen(false)}>Supplies</Link></li>
          <li><Link to="/about" onClick={() => setIsOpen(false)}>About Me</Link></li>
          <li><Link to="/faq" onClick={() => setIsOpen(false)}>FAQ</Link></li>
          <li><Link to="/contact" onClick={() => setIsOpen(false)}>Contact</Link></li>
        </ul>
      </div>

      {/* Overlay */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black opacity-50 z-40"
          onClick={() => setIsOpen(false)}
        ></div>
      )}
    </>
  );
}

export default Navbar;
