import React from 'react';
import heroImage from ''; // update path based on your project

const HeroBanner = () => {
  return (
    <section className="flex flex-col md:flex-row justify-between items-center bg-[#f3ece5] text-black px-6 py-16 md:py-24">
      <div className="max-w-xl mb-10 md:mb-0">
        <h1 className="text-4xl md:text-5xl font-bold leading-tight mb-4">
          Premium Hair Systems for Modern Men
        </h1>
        <p className="text-lg md:text-xl mb-6">Custom cuts, bold confidence.</p>
        <a
          href="/shop"
          className="bg-[#cc5c2f] text-white px-6 py-3 rounded hover:bg-[#b34f28] transition"
        >
          Shop Units
        </a>
      </div>
      <img
        src={heroImage}
        alt="Styled man"
        className="w-full md:w-[400px] rounded shadow-lg"
      />
    </section>
  );
};

export default HeroBanner;
