import React from 'react';
import heroImage from '../assets/hero1.png';

const HeroBanner = () => {
  return (
    <section className="relative bg-[#f3ece5] text-black overflow-hidden h-[90vh] md:h-screen">
      {/* Large, anchored image */}
      <img
        src={heroImage}
        alt="Styled man"
        className="absolute bottom-0 left-0 w-[120%] max-w-none h-auto object-contain z-0"
      />

      {/* Text content layered above */}
      <div className="relative z-10 max-w-4xl ml-auto px-8 py-16 md:px-24 md:py-32 text-right">
        <h1 className="text-4xl md:text-5xl font-bold leading-tight mb-4">
          PREMIUM HAIR SYSTEMS FOR MODERN MEN
        </h1>
        <p className="text-lg md:text-xl mb-6">Custom cuts, bold confidence.</p>
        <a
          href="/shop"
          className="bg-[#cc5c2f] text-white px-6 py-3 rounded hover:bg-[#b34f28] transition"
        >
          Shop Units
        </a>
      </div>
    </section>
  );
};

export default HeroBanner;
