import React from 'react';
import Navbar from './navbar';
import HeroBanner from './components/HeroBanner';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import {
  BeakerIcon,
  ScissorsIcon,
  ShoppingCartIcon
} from '@heroicons/react/24/solid';

function Home() {
  return (
    <div>
      <HeroBanner />
      <section className="bg-white text-black py-24 px-6 md:px-16 text-center">
        {/* Three-step content */}
        <h2 className="text-4xl md:text-6xl font-extrabold tracking-tight mb-6 uppercase">
          Get Started With a Hair System
        </h2>
        <p className="text-lg md:text-2xl max-w-2xl mx-auto mb-12">
          Discover the confidence of a custom-fit, natural-looking solution tailored just for you.
        </p>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {[
            {
              Icon: BeakerIcon,
              title: '1. Pick Your Hair Color',
              text: 'Select the shade that best matches your natural hair.',
            },
            {
              Icon: ScissorsIcon,
              title: '2. Get a Template Cut',
              text: 'Send us your head measurements using our instructions.',
            },
            {
              Icon: ShoppingCartIcon,
              title: '3. Order a Starter Kit',
              text: 'Receive your unit and start your hair journey!',
            }
          ].map(({ Icon, title, text }) => (
            <div key={title} className="p-6 border rounded-lg shadow-lg flex flex-col items-center">
              <Icon className="h-12 w-12 text-black fill-current mb-4" />
              <h3 className="text-xl md:text-2xl font-semibold mb-3 uppercase">{title}</h3>
              <p>{text}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

