// src/components/HeroBanner.jsx

import React from 'react';
import heroImage from '../assets/hero1.png';

const HeroBanner = () => {
  return (
    <section className="relative bg-[#f3ece5] text-black overflow-hidden h-screen flex items-center">
      {/* Background imageâ€”anchor right, cover full height */}
      <img
        src={heroImage}
        alt="Styled man"
        className="absolute inset-y-0 right-0 h-full object-cover object-right z-0"
      />

      {/* Text container moved right with padding */}
      <div className="relative z-10 max-w-xl ml-auto pr-16 pl-8 text-right">
        <h1 className="text-6xl md:text-7xl font-extrabold leading-tight tracking-tight mb-6 uppercase">
          Premium Hair Systems
        </h1>
        <p className="text-2xl md:text-3xl font-medium mb-10">
          Custom Cuts. Bold Confidence. Real Results.
        </p>
        <a
          href="/shop"
          className="inline-block bg-[#cc5c2f] text-white text-xl px-8 py-4 rounded-lg hover:bg-[#b34f28] transition transform hover:scale-105"
        >
          Shop Units
        </a>
      </div>
    </section>
  );
};

export default HeroBanner;
