import React from 'react';
import heroImage from '../assets/hero1.png';

const HeroBanner = () => {
  return (
    <section className="relative bg-[#f3ece5] text-black py-16 md:py-24 overflow-hidden">
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center px-8 md:px-16 gap-12">
        {/* Text Section */}
        <div className="md:w-1/2">
          <h1 className="text-4xl md:text-5xl font-bold leading-tight mb-4">
            PREMIUM HAIR SYSTEMS FOR MODERN MEN
          </h1>
          <p className="text-lg md:text-xl mb-6">Custom cuts, bold confidence.</p>
          <a
            href="/shop"
            className="bg-[#cc5c2f] text-white px-6 py-3 rounded hover:bg-[#b34f28] transition"
          >
            Shop Units
          </a>
        </div>

        {/* Image Section */}
        <div className="md:w-1/2 flex justify-center">
          <img
            src={heroImage}
            alt="Styled man"
            className="w-[500px] md:w-[800px] lg:w-[650px] max-w-full"
          />
        </div>
      </div>
    </section>
  );
};

export default HeroBanner;
