import React from 'react';
import heroImage from '../assets/hero1.png';

const HeroBanner = () => {
  return (
    <section className="relative bg-[#f3ece5] text-black overflow-hidden h-screen flex items-center">
      {/* Full-width background image, anchored left and right */}
      <img
        src={heroImage}
        alt="Styled man"
        className="absolute top-0 left-0 right-0 h-full w-full object-contain object-right z-0"
      />

      {/* Text content: left-aligned and shifted inward toward center */}
      <div className="relative z-10 max-w-2xl pl-16 pr-8 text-left">
        <h1 className="text-5xl md:text-7xl font-extrabold leading-tight tracking-tight mb-6 uppercase">
          Premium Hair Systems
        </h1>
        <p className="text-2xl md:text-3xl font-medium mb-8">
          Custom Cuts. Bold Confidence. Real Results.
        </p>
        <a
          href="/shop"
          className="bg-[#cc5c2f] text-white text-lg md:text-xl px-6 py-4 rounded hover:bg-[#b34f28] transition"
        >
          Shop Units
        </a>
      </div>
    </section>
  );
};

export default HeroBanner;
